function gammaIncomplete(a, x) {
  let g = 1 / a;
  for (let i = 1; i < 100; i++) {
    g *= (1 + (x / i));
  }
  return g * Math.exp(-x);
}

function gamma(a) {
  const g = 7;
  const p = [
    0.99999999999980993, 676.5203681218851, -1259.1392167224028,
    771.32342877765313, -176.61502916214059, 12.507343278686905,
    -0.13857109526572012, 9.9843695780195716e-6,
    1.5056327351493116e-7
  ];

  if (a < 0.5) {
    return Math.PI / (Math.sin(Math.PI * a) * gamma(1 - a));
  }

  a -= 1;
  let x = p[0];
  for (let i = 1; i < g + 2; i++) {
    x += p[i] / (a + i);
  }

  const t = a + g + 0.5;
  return Math.sqrt(2 * Math.PI) * Math.pow(t, a + 0.5) * Math.exp(-t) * x;
}

function chi2PValue(chi2, ddl) {
  const upperGamma = gamma(ddl / 2);
  const lowerGamma = gammaIncomplete(ddl / 2, chi2 / 2);
  const cdf = lowerGamma / upperGamma;
  return (1 - cdf);
}

const expected = 85;

return items.map(item => {
  const data = item.json;

  const values = [
    { key: 'pourcentage_name', value: data.pourcentage_name },
    { key: 'pourcentage_address', value: data.pourcentage_address },
    { key: 'pourcentage_description', value: data.pourcentage_description }
  ];

  // Filtrer uniquement les valeurs positives
  const validValues = values.filter(v => v.value !== -1);

  const ddl = validValues.length;

  // Si aucune valeur valide, retourner le résultat brut sans test
  if (ddl === 0) {
    return {
      json: {
        ...data,
        chi2: null,
        ddl: 0,
        p_value: null,
        prob_reelle_chinois: null
      }
    };
  }

  // Calcul chi² uniquement sur les valeurs valides
  const chi2 = validValues.reduce((sum, v) => {
    return sum + Math.pow(v.value - expected, 2);
  }, 0) / expected;

  // Calcul probabilité réelle (moyenne des valeurs valides)
  const probReelleChinois = validValues.reduce((sum, v) => sum + v.value, 0) / ddl;

  const pValue = chi2PValue(chi2, ddl);

  return {
    json: {
      ...data,
      chi2: Number(chi2.toFixed(4)),
      ddl,
      p_value: Number(pValue.toFixed(6)),
      prob_reelle_chinois: Number(probReelleChinois.toFixed(2))
    }
  };
});

