// Lieux fr√©quent√©s par la communaut√© chinoise
const lieuxChinois = [
  'quartier chinois', 'antaninarenina', 'analakely', 'andraharo',
  'behoririka', 'ankarahabe', 'ambodivona', 'ambatomaro',
  'andohalo', 'arabe', 'andrinampoinimerina', 'tanjombato'
];

// Noms de famille chinois
const motsChinois = [
  "wang", "li", "chen", "liu", "zhang", "huang", "zhou", "lin", "yang",
  "wu", "xu", "zheng", "gao", "sun", "ma", "zhao", "guo", "he", "luo",
  "tang", "feng", "deng", "cai", "xie", "song", "dong", "han", "cao",
  "yu", "pan", "wei", "jiang", "xiong", "fang", "yuan", "du", "qian",
  "shi", "zhong", "dai"
];

// Mots pour d√©tecter le d√©cor chinois
const decorKeywords = [
  'chinois', 'chinese', 'asiatique', 'asian',
  'lantern', 'bamboo', 'pagoda', 'dragon',
  'oriental', 'zen', 'feng shui', 'typical', 'traditional'
];
const decorMots = new RegExp(decorKeywords.join('|'), 'i');

// Mots-cl√©s pour sp√©cialit√©s chinoises
const specialitesChinoises = /chinese|mandarin|sichuan|cantonese|dim sum|pekin|hunan|hakka/i;

// Pond√©rations
const poids = {
  nomChinois: 1,
  specialiteOk: 1,
  emplacementOk: 1,
  decorOk: 0.7,
  ratingOk: 0.6,
  avisSuffisants: 0.6
};

// Calcul du poids total
const poidsTotal = Object.values(poids).reduce((a, b) => a + b, 0);

// Initialiser les r√©sultats
let results = [];

for (let r of data) {
  const nom = (r.nom || '').toLowerCase();
  const spec = (r.specialite || '').toLowerCase();
  const adresse = (r.adresse || '').toLowerCase();
  const decor = (r.decor || '').toLowerCase();
  const rating = r.rating || 0;
  const reviews = r.reviews || 0;

  // Crit√®res binaires
  const nomChinois = /[\u4e00-\u9fff]/.test(nom) || motsChinois.some(mot => nom.includes(mot)) ? 1 : 0;
  const specialiteOk = specialitesChinoises.test(spec) ? 1 : 0;
  const emplacementOk = lieuxChinois.some(lieu => adresse.includes(lieu)) ? 1 : 0;
  const decorOk = decorMots.test(decor) ? 1 : 0;
  const ratingOk = rating >= 4 ? 1 : 0;
  const avisSuffisants = reviews >= 5 ? 1 : 0;

  // Score pond√©r√©
  const scoreTotal =
    nomChinois * poids.nomChinois +
    specialiteOk * poids.specialiteOk +
    emplacementOk * poids.emplacementOk +
    decorOk * poids.decorOk +
    ratingOk * poids.ratingOk +
    avisSuffisants * poids.avisSuffisants;

  const probabiliteVraiRestaurantChinois = scoreTotal / poidsTotal;

  // Ajouter aux r√©sultats
  results.push({
    json: {
      ...r,
      probabiliteVraiRestaurantChinois,
      checks: {
        nomChinois,
        specialiteOk,
        emplacementOk,
        decorOk,
        ratingOk,
        avisSuffisants
      }
    }
  });
}

// Retourner les r√©sultats au format n8n
return results;



