function chi2(a, b, c, d) {
  const total = a + b + c + d;
  const row1 = a + b;
  const row2 = c + d;
  const col1 = a + c;
  const col2 = b + d;

  const expectedA = (row1 * col1) / total;
  const expectedB = (row1 * col2) / total;
  const expectedC = (row2 * col1) / total;
  const expectedD = (row2 * col2) / total;

  if ([expectedA, expectedB, expectedC, expectedD].some(v => v === 0)) {
    return null;
  }

  const chi2Stat =
    ((a - expectedA) ** 2) / expectedA +
    ((b - expectedB) ** 2) / expectedB +
    ((c - expectedC) ** 2) / expectedC +
    ((d - expectedD) ** 2) / expectedD;

  return chi2Stat;
}

const seuil = 0.5;
const data = items.map(item => item.json);

// Liste des crit√®res √† tester
const criteres = ['nomChinois', 'specialiteOk', 'emplacementOk', 'decorOk', 'ratingOk', 'avisSuffisants'];

// Calcul des chi2 par crit√®re
let criteresSignificatifs = {};

criteres.forEach(critere => {
  let a = 0, b = 0, c = 0, d = 0;

  data.forEach(r => {
    const vraiChinois = r.probabiliteVraiRestaurantChinois > seuil ? 1 : 0;
    const critereActif = r.checks[critere] || 0;

    if (critereActif === 1 && vraiChinois === 1) a++;
    if (critereActif === 1 && vraiChinois === 0) b++;
    if (critereActif === 0 && vraiChinois === 1) c++;
    if (critereActif === 0 && vraiChinois === 0) d++;
  });

  const chi2Valeur = chi2(a, b, c, d);
  criteresSignificatifs[critere] = chi2Valeur !== null && chi2Valeur > 3.84;
});

// Filtrage principal
let filtered = data.filter(r => {
  if (r.probabiliteVraiRestaurantChinois <= seuil) return false;

  const nom = (r.nom || '').toLowerCase();
  const quartier = (r.quartier || '').toLowerCase();

  // Exclure si nom contient "bazar du quartier" ou "bazary"
  if (nom.includes("bazar du quartier") || nom.includes("bazary")) return false;
  if (quartier.includes("bazary")) return false;

  // Compter le nombre de crit√®res significatifs vrais
  let count = 0;
  criteres.forEach(critere => {
    if (criteresSignificatifs[critere] && r.checks[critere] === 1) {
      count++;
    }
  });

  return count >= 1;
});

if (filtered.length < 20) {
  const restSorted = data
    .filter(r => !filtered.includes(r))
    .filter(r => {
      const nom = (r.nom || '').toLowerCase();
      const quartier = (r.quartier || '').toLowerCase();
      return !nom.includes("bazar du quartier") && !nom.includes("bazary") && !quartier.includes("bazary");
    })
    .sort((a, b) => b.probabiliteVraiRestaurantChinois - a.probabiliteVraiRestaurantChinois);

  filtered = filtered.concat(restSorted.slice(0, 26 - filtered.length));
}
filtered = filtered.slice(0, 20);

// Format final pour n8n
return filtered.map(r => ({ json: r }));

function escapeHtml(text) {
  if (typeof text !== 'string') return 'N/A';
  return text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

const restaurants = items.map(item => item.json);

let html = `
