  <div class="grid" id="restaurantGrid">
`;
for (let i = 0; i < restaurants.length; i++) {
  const r = restaurants[i];
  const idMap = `miniMap${i}`;
  html += `
    <div class="restaurant">
      <img src="${escapeHtml(r.photo || '')}" alt="photo de ${escapeHtml(r.nom || 'Restaurant')}" />
      <div class="info">
        <p><strong>${escapeHtml(r.nom || 'Nom inconnu')}</strong></p>
        <p><em>${escapeHtml(r.specialite || 'Sp√©cialit√© inconnue')}</em></p>
        <p><strong>Adresse :</strong> ${escapeHtml(r.adresse || 'Adresse inconnue')}</p>  
        <p><strong>T√©l√©phone :</strong> ${escapeHtml(r.contact || 'Non disponible')}</p> 
        <p><strong>Site web :</strong> ${escapeHtml(r.site || 'Aucun site')}</p>          
        <p><strong>Coordonn√©es GPS :</strong> ${escapeHtml(String(r.latitude || 'N/A'))}, ${escapeHtml(String(r.longitude || 'N/A'))}</p>
        <div class="mini-map" id="${idMap}"></div>
      </div>
    </div>
  `;
}


html += `
  </div>

  <script src="https://unpkg.com/leaflet@1.9.3/dist/leaflet.js"></script>

  <script>
    const restaurants = ${JSON.stringify(restaurants)};

    // Filtre recherche texte
    document.getElementById("search").addEventListener("input", function() {
      const query = this.value.toLowerCase();
      const cards = document.querySelectorAll(".restaurant");
      cards.forEach(card => {
        const text = card.innerText.toLowerCase();
        card.style.display = text.includes(query) ? "block" : "none";
      });
    });

    // Marqueur custom rouge avec ombre
    const redIcon = L.icon({
      iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
      iconSize: [40, 40],
      iconAnchor: [20, 40],
      popupAnchor: [0, -40],
      shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
      shadowSize: [41, 41],
      shadowAnchor: [12, 41]
    });

    // Carte principale centr√©e sur Antananarivo
    const mainMap = L.map("map").setView([-18.9137, 47.5361], 13);
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "¬© OpenStreetMap contributors"
    }).addTo(mainMap);

    restaurants.forEach((r, i) => {
      if (r.latitude && r.longitude) {
        // Marqueur sur la grande carte
        L.marker([r.latitude, r.longitude]).addTo(mainMap).bindPopup(r.nom || "Restaurant");

        // Mini-carte avec effet 3D et marqueur custom
        const miniMapId = "miniMap" + i;
        const miniMapDiv = document.getElementById(miniMapId);
        if (miniMapDiv) {
          const miniMap = L.map(miniMapId, {
            center: [r.latitude, r.longitude],
            zoom: 15,
            zoomControl: false,
            attributionControl: false,
            dragging: true,
            scrollWheelZoom: true,
            doubleClickZoom: true,
            boxZoom: false,
            keyboard: false,
          });
          L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png").addTo(miniMap);
          L.marker([r.latitude, r.longitude], { icon: redIcon }).addTo(miniMap);
        }
      }
    });
  </script>

</body>
