// Function to calculate Chi-squared statistic
function calculateChiSquared(observed, expected) {
  let chiSquared = 0;
  const parameters = ['name', 'location', 'text', 'authors', 'rating'];
  for (let param of parameters) {
    const obs = observed[param] || 0;
    const exp = expected[param] || 0.01; // Avoid division by zero
    chiSquared += ((obs - exp) ** 2) / exp;
  }
  return chiSquared;
}

return items.map(item => {
  const restaurants = item.json.restaurants || [];
  return {
    json: {
      restaurants: restaurants.map(restaurant => {
        // Retrieve scores (0 if absent)
        const nameScore = restaurant.name_chineseness_score || 0;
        const textScore = restaurant.text_chineseness_score || 0;
        const authorsScore = restaurant.authors_chineseness_score || 0;
        const locationScore = restaurant.location_chineseness_score || 0;
        const ratingScore = restaurant.rating_chineseness_score || 0;

        // Check discriminative rule
        const isTextUnavailable = !restaurant.all_text || restaurant.all_text.trim() === '' || textScore === 0;
        const isAuthorsUnavailable = !restaurant.all_authors || restaurant.all_authors.trim() === '' || authorsScore === 0;
        const isHighNameScore = nameScore >= 0.7;
        const isHighLocationScore = locationScore >= 0.7;

        let classification;
        let globalScore;

        if (isHighNameScore && isHighLocationScore && isTextUnavailable && isAuthorsUnavailable) {
          // Discriminative rule: force "Vraiment chinois"
          classification = 'Vraiment chinois';
          globalScore = 1.0;
        } else {
          // Calculate Chi-squared for each class
          const observed = {
            name: nameScore,
            location: locationScore,
            text: textScore,
            authors: authorsScore,
            rating: ratingScore
          };
          const chiSquaredResults = {};
          for (let className in expectedScores) {
            chiSquaredResults[className] = calculateChiSquared(observed, expectedScores[className]);
          }

          // Choose class with lowest Chi-squared
          const bestClass = Object.keys(chiSquaredResults).reduce((a, b) =>
            chiSquaredResults[a] < chiSquaredResults[b] ? a : b
          );
          classification = bestClass;

          // Calculate weighted score for consistency (optional)
          globalScore = (
            nameScore * weights.name +
            textScore * weights.text +
            authorsScore * weights.authors +
            locationScore * weights.location +
            ratingScore * weights.rating
          );
          if(globalScore < 0.3 && classification == 'Moyennement chinois'){
            classification = 'Pas chinois';
          }
        }

        return {
          ...restaurant,
          global_chineseness_score: globalScore,
          classification: classification
        };
      })
    }
  };
});

